import React from 'react';
import { AVATAR_IMG, LOGO_URL } from '../../services/urlsservice';
import { getAuth, signOut } from "firebase/auth";
import { useNavigate } from 'react-router-dom';
import app from '../../utils/firebase';
import { useDispatch } from 'react-redux';
import { removeUser } from '../../utils/store/userSlice';

const BrowserHeader = () => {
    const navigate = useNavigate()
    const dispatch = useDispatch()
    function handleLogout(){
        const auth = getAuth(app);
        signOut(auth).then(() => {
          dispatch(removeUser())
          navigate('/')
        }).catch((error) => {
          // An error happened.
        });
    }
  return (
    <header className="bg-black text-white fixed w-full z-50">
      <div className="flex items-center justify-between p-4">
      <img src={LOGO_URL} className='w-32 h-12'/>

        {/* Navigation links */}
        <div className="hidden md:flex space-x-6">
          <a href="/" className="hover:text-gray-300">Home</a>
          <a href="/tv-shows" className="hover:text-gray-300">TV Shows</a>
          <a href="/movies" className="hover:text-gray-300">Movies</a>
          <a href="/new" className="hover:text-gray-300">New & Popular</a>
          <a href="/my-list" className="hover:text-gray-300">My List</a>
        </div>

        {/* Right section: user avatar or profile actions */}
        <div className="flex items-center space-x-4">
          <div className="relative">
            <img
              src={AVATAR_IMG}
              className="w-8 h-8 hover:ring-2 hover:ring-red-600"
            />
          </div>
           <button onClick={handleLogout}>Logout</button>
        </div>
      </div>
    </header>
  );
};

export default BrowserHeader;



