import React, { useRef } from "react";
import client from "../../utils/openAi";

const GptSearchBar = () => {
  let refrence = useRef
 async function handleGptSearch(){
  const chatCompletion = await client.chat.completions.create({
    messages: [{ role: 'user', content: 'Say this is a test' }],
    model: 'gpt-3.5-turbo',
  });
  }
  return (
    <div className="h-40 pt-20">
      <div class="flex items-center w-full max-w-md mx-auto bg-gray-100 rounded-full px-4 py-2 shadow-md">
        <input
          type="text"
          placeholder="Ask me anything..."
          class="flex-grow bg-transparent outline-none text-sm text-gray-800 placeholder-gray-500"
        />
        <button class="ml-2 bg-black text-white px-4 py-2 rounded-full hover:bg-black hover:bg-opacity-80 transition" onClick={handleGptSearch}>
          Search
        </button>
      </div>
    </div>
  );
};

export default GptSearchBar;
