import React, { useState } from "react";
import { FiSearch } from "react-icons/fi";

const SearchButton = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [isFocused, setIsFocused] = useState(false);

  return (
    <div
      className={`flex items-center border ${
        isFocused ? "border-white" : "border-gray-500"
      } rounded-full px-4 py-2 bg-black w-full max-w-md transition-all duration-300`}
    >
      {/* Search Icon */}


      {/* Input Field */}
      <input
        type="text"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        onFocus={() => setIsFocused(true)}
        onBlur={() => setIsFocused(false)}
        placeholder="Search for movies, TV shows, and more"
        className="bg-transparent text-white outline-none ml-2 w-full text-sm"
      />

      {/* Clear Button */}
      {searchTerm && (
        <button
          onClick={() => setSearchTerm("")}
          className="text-gray-500 hover:text-gray-300 text-sm ml-2"
        >
          Clear
        </button>
      )}
    </div>
  );
};

export default SearchButton;
