import React from "react";
import { MOVIE_IMG } from "../../services/urlsservice";

const MovieCard = ({ movieList ,title}) => {
  return (

    <div className="flex overflow-x-scroll py-9 bg-black ">
      {movieList.map((movie) => (
        <div key={movie.id} className="group relative min-w-64 w-64 h-60 mx-5 cursor-pointer overflow-hidden rounded-sm shadow-lg">
          <img
          src={MOVIE_IMG+movie.backdrop_path}
            alt={movie.title}
            className="w-full h-full object-cover transition-transform duration-300 group-hover:scale-110"/>

          <div className="absolute inset-0 bg-gradient-to-t from-black via-transparent to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
          <div className="absolute bottom-0 left-0 p-4 text-white space-y-2">
            <h3 className="text-lg font-bold">{movie.title}</h3>
            <p className="text-sm opacity-0 group-hover:opacity-100 transition-opacity duration-300 line-clamp-3">
              {movie.overview}
            </p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default MovieCard;
