import React from "react";

const ShimmerCard = () => {
  return (
    <div className="w-48 h-64 bg-gray-300 rounded-md overflow-hidden relative">
      {/* Shimmer Image */}
      <div className="w-full h-36 bg-gray-200 shimmer"></div>

      {/* Shimmer Text */}
      <div className="p-3 space-y-2">
        <div className="w-3/4 h-4 bg-gray-200 shimmer"></div>
        <div className="w-1/2 h-4 bg-gray-200 shimmer"></div>
      </div>
    </div>
  );
};

export default ShimmerCard;
