import React, { useRef } from "react";
import client from "../../utils/openAi";

const GptSearchBar = () => {
  let reference = useRef(null)
 async function handleGptSearch(){
  const chatCompletion = await client.chat.completions.create({
    messages: [{ role: 'user', content: reference.current.value }],
    model: 'gpt-3.5-turbo',
  });
  console.log(reference.current.value);

  }
  return (
    <div className="h-40 pt-20">
      <div class="flex items-center w-full min-w-72 mx-auto bg-gray-100 rounded-full px-4 py-1 shadow-md">
        <input
        ref={reference}
          type="text"
          placeholder="Ask me anything..."
          className="flex-grow bg-transparent outline-none text-sm text-gray-800 placeholder-gray-500"
        />
        <button class="ml-2 bg-black text-white px-4 py-2 rounded-full hover:bg-black hover:bg-opacity-80 transition" onClick={handleGptSearch}>
          Search
        </button>
      </div>
    </div>
  );
};

export default GptSearchBar;
