import { getValue } from "@testing-library/user-event/dist/utils";
import React, { useState } from "react";
import { useForm } from "react-hook-form";
import { Link } from "react-router-dom";

const LoginForm = () => {
  let [isSignIn,setIsSignIn] = useState(true)
  let[type,setType] = useState(true)
  let {register,handleSubmit,control,formState:{errors}} = useForm()
  function handleForm(e){
   e.preventDefault()
   setIsSignIn(!isSignIn)
  }
  function handleType(e){
    e.preventDefault()
    setType(!type)
  }
  function submitForm(data){
    console.log(data);

  }
  return (

      <div className="bg-black bg-opacity-80 p-8  shadow-xl shadow-black max-w-sm w-full min-w-80 absolute my-44 mx-auto right-0 left-0 ">
        <h2 className="text-3xl font-bold text-white mb-6">
         {isSignIn ? " Sign In" :" Sign Up"}
        </h2>
        <form onSubmit={handleSubmit(submitForm)} noValidate>
        {
          !isSignIn &&
          <div className="">
          <strong className="text-red-600 text-sm">{errors?.name?.message}</strong>
            <input
              type="text"
              className="w-full px-4 py-4 bg-opacity-50 bg-black text-white focus:outline-none focus:ring-2 focus:ring-red-600 mb-4"
              placeholder="Full name"
              {...register('name',{
                required:{
                  value:true,
                  message:"*"
                },
                minLength:{
                  value:5,
                  message:'invalid name'
                }
              })}
            />

             <strong className="text-red-600 text-sm">{errors?.mobileNumber?.message}</strong>
             <input
              type="text"
              className="w-full px-4 py-4 bg-opacity-50 bg-black text-white focus:outline-none focus:ring-2 focus:ring-red-600 mb-4"
              placeholder="Mobile Number"
              {
                ...register('mobileNumber',{
                  required:{
                    value:true,
                    message:'*'
                  },
                  pattern:{
                    value:/^\s*(?:\+?(\d{1,3}))?([-. (]*(\d{3})[-. )]*)?((\d{3})[-. ]*(\d{2,4})(?:[-.x ]*(\d+))?)\s*$/,
                    message:'enter valid mobile number'
                  }
                })
              }
            />
          </div>

        }
          <div className="mb-4">
            <input
              type="email"
              className="w-full px-4 py-4 bg-opacity-50 bg-black text-white focus:outline-none focus:ring-2 focus:ring-red-600"
              placeholder={isSignIn ?"Email or mobile number" : "Email"}
              {
                ...register
              }
            />
          </div>
          <div className="mb-6">
            <input 
              type={type ? 'password' :"text"}
              className="w-full px-4 py-4 bg-opacity-50 bg-black text-white  focus:outline-none focus:ring-2 focus:ring-red-600"
              placeholder="Password"
            />
            <button className="text-gray-500 text-sm " onClick={handleType}>show password </button>
          </div>
          <button
            type="submit"
            className="w-full py-2 bg-red-600 text-white font-bold rounded-lg hover:bg-red-700 transition"
          >
            {isSignIn ? " Login" :" Sign Up"}
          </button>
        </form>
        <div className="flex justify-between items-center text-gray-500 text-sm mt-4">
          <p>
            <input type="checkbox" className="mr-2" />
            Remember Me
          </p>
          <a href="#" className="hover:underline">
            Need Help?
          </a>
        </div>
        <p className="text-gray-500  mt-6">
        {isSignIn ? "New to Netflix? " :"Already a user? "}
          {" "}
          <Link onClick={handleForm}  className="text-white hover:underline">
        {isSignIn ? "  Sign up now. " :"Login "}

          </Link>
        </p>
        <p className="text-sm text-gray-500 mt-6">This page is protected by Google reCAPTCHA to ensure you're not a bot. </p>
      </div>

  );
};

export default LoginForm;
