import React, { useState } from "react";
import { Link } from "react-router-dom";
import { EYE_URL } from "../services/urlsservice";

const LoginForm = () => {
  let [isSignIn,setIsSignIn] = useState(true)
  let[type,setType] = useState(true)
  function handleForm(e){
   e.preventDefault()
   setIsSignIn(!isSignIn)
  }
  function handleType(e){
    e.preventDefault()
    setType(!type)
  }
  return (

      <div className="bg-black bg-opacity-80 p-8  shadow-xl shadow-black max-w-sm w-full absolute my-44 mx-auto right-0 left-0 ">
        <h2 className="text-3xl font-bold text-white mb-6">
         {isSignIn ? " Sign In" :" Sign Up"}
        </h2>
        <form>
          <div className="mb-4">

            <input
              type="email"
              className="w-full px-4 py-4 bg-opacity-50 bg-black text-white focus:outline-none focus:ring-2 focus:ring-red-600"
              placeholder="Email or mobile number"
            />
          </div>
          <div className="mb-6">
            <input 
              type={type ? 'password' :"text"}
              className="w-full px-4 py-4 bg-opacity-50 bg-black text-white  focus:outline-none focus:ring-2 focus:ring-red-600"
              placeholder="Password"
            />
            <button className="text-white relative -top-12 left-80  " onClick={handleType}>
              <img src={EYE_URL} className="bg-white size-5" />
            </button>
          </div>
          <button
            type="submit"
            className="w-full py-2 bg-red-600 text-white font-bold rounded-lg hover:bg-red-700 transition"
          >
            {isSignIn ? " Login" :" Sign Up"}
          </button>
        </form>
        <div className="flex justify-between items-center text-gray-500 text-sm mt-4">
          <p>
            <input type="checkbox" className="mr-2" />
            Remember Me
          </p>
          <a href="#" className="hover:underline">
            Need Help?
          </a>
        </div>
        <p className="text-gray-500  mt-6">
        {isSignIn ? "New to Netflix? " :"Already a user? "}
          {" "}
          <Link onClick={handleForm}  className="text-white hover:underline">
        {isSignIn ? "  Sign up now. " :"Login "}

          </Link>
        </p>
        <p className="text-sm text-gray-500 mt-6">This page is protected by Google reCAPTCHA to ensure you're not a bot. </p>
      </div>

  );
};

export default LoginForm;
